package com.marshalchen.common.uimodule.imageprocessing.filter.blend;import com.marshalchen.common.uimodule.imageprocessing.filter.MultiInputFilter;/** * Applies a darken blend of two images. Takes the dark colour of the two images for each pixel. * @author Chris Batt */public class DarkenBlendFilter extends MultiInputFilter {	public DarkenBlendFilter() {		super(2);	}		@Override	protected String getFragmentShader() {		return 				 "precision mediump float;\n" 				+"uniform sampler2D "+UNIFORM_TEXTURE0+";\n"  				+"uniform sampler2D "+UNIFORM_TEXTUREBASE+1+";\n" 				+"varying vec2 "+VARYING_TEXCOORD+";\n"							  		+"void main(){\n"		  		+"   vec4 color1 = texture2D("+UNIFORM_TEXTURE0+","+VARYING_TEXCOORD+");\n"		  		+"   vec4 color2 = texture2D("+UNIFORM_TEXTUREBASE+1+","+VARYING_TEXCOORD+");\n"	   			+"   gl_FragColor = vec4(min(color2.rgb * color1.a, color1.rgb * color2.a) + color2.rgb * (1.0 - color1.a) + color1.rgb * (1.0 - color2.a), 1.0);\n"		  		+"}\n";		}	}